allow:
Bash(/home/personal/Developer/youtube-downloader-gui/src/index.css << 'EOF'

/* ============================================
   BATCH COUNTDOWN & PO TOKEN STATUS
   ============================================ */

/* Batch pause countdown - prominent, pulsing */
.status-countdown {
  color: var\(--accent\);
  animation: countdownPulse 1.5s ease-in-out infinite;
  font-weight: 500;
}

/* Download delay countdown - subtle */
.status-countdown-small {
  color: var\(--text-muted\);
  font-size: 0.85em;
}

@keyframes countdownPulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

/* Batch info badge */
.status-batch-info {
  display: inline-block;
  border: 1px solid var\(--border\);
  border-radius: 4px;
  padding: 2px 8px;
  font-size: 0.7em;
  margin-left: 8px;
  font-weight: 500;
  letter-spacing: 0.05em;
  text-transform: uppercase;
  color: var\(--text-secondary\);
}

/* PO Token indicator in status bar */
.pot-indicator {
  display: inline-flex;
  align-items: center;
  padding: 2px 6px;
  border-radius: 4px;
  font-size: 0.65em;
  font-weight: 600;
  letter-spacing: 0.1em;
  cursor: default;
  transition: all 0.2s ease;
}

.pot-indicator.active {
  background: rgba\(34, 197, 94, 0.15\);
  color: #22c55e;
  border: 1px solid rgba\(34, 197, 94, 0.3\);
}

.pot-indicator.inactive {
  background: rgba\(239, 68, 68, 0.15\);
  color: #ef4444;
  border: 1px solid rgba\(239, 68, 68, 0.3\);
}

/* PO Token status badge in settings */
.pot-status-badge {
  display: inline-flex;
  align-items: center;
  padding: 4px 10px;
  border-radius: 4px;
  font-size: 0.7em;
  font-weight: 600;
  letter-spacing: 0.1em;
  text-transform: uppercase;
}

.pot-status-badge.running {
  background: rgba\(34, 197, 94, 0.15\);
  color: #22c55e;
  border: 1px solid rgba\(34, 197, 94, 0.3\);
}

.pot-status-badge.stopped {
  background: rgba\(239, 68, 68, 0.15\);
  color: #ef4444;
  border: 1px solid rgba\(239, 68, 68, 0.3\);
}

/* Status indicator batch paused state */
.status-indicator.batch-paused {
  background: var\(--warning\);
  animation: pulse 2s ease-in-out infinite;
}

.status-indicator.batch-paused::after {
  border-color: var\(--warning\);
  animation: statusRing 2s ease-in-out infinite;
}

/* Status bar right section */
.status-bar-right {
  display: flex;
  align-items: center;
  gap: 12px;
}

/* Settings error message */
.setting-error {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 8px 12px;
  margin-top: 12px;
  background: rgba\(239, 68, 68, 0.1\);
  border: 1px solid rgba\(239, 68, 68, 0.3\);
  border-radius: 6px;
  color: #ef4444;
  font-size: 0.85em;
}
EOF)
